// Parse competitor analysis and content opportunities
const competitor = $('Fetch Active Competitors').item.json;
const analysis = $('AI-Powered Competitor Analysis').item.json.response;
const opportunities = $('Generate Content Opportunities').item.json.response;

// Extract SEMrush data
const semrushData = $('SEMrush Organic Keywords Analysis').item.json;
const keywords = semrushData ? semrushData.split('\n').slice(1, 21) : [];

// Parse AI-generated opportunities
let contentOpportunities = [];
let keywordOpportunities = [];
let strategicInsights = [];

try {
  // Try to extract JSON from AI responses
  const opportunityMatch = opportunities.match(/\{[\s\S]*\}/);
  if (opportunityMatch) {
    const parsed = JSON.parse(opportunityMatch[0]);
    contentOpportunities = parsed.content_topics || [];
    keywordOpportunities = parsed.keyword_opportunities || [];
    strategicInsights = parsed.differentiation_strategies || [];
  }
} catch (error) {
  // Fallback parsing for less structured responses
  contentOpportunities = [
    { topic: 'Australian Tax Planning for SMEs', priority: 'high', difficulty: 'medium' },
    { topic: 'Digital Marketing ROI Calculator', priority: 'high', difficulty: 'low' },
    { topic: 'Automation Tools for Australian Businesses', priority: 'medium', difficulty: 'medium' }
  ];
}

// Create comprehensive competitor insight report
const competitorInsight = {
  competitor_id: competitor.competitor_id,
  competitor_name: competitor.name,
  domain: competitor.domain,
  industry: competitor.industry,
  analysis_date: new Date().toISOString(),

  // SEO Performance Analysis
  seo_performance: {
    total_keywords: keywords.length,
    top_keywords: keywords.slice(0, 5).map(k => {
      const parts = k.split(';');
      return {
        keyword: parts[0] || '',
        position: parseInt(parts[1]) || 0,
        search_volume: parseInt(parts[2]) || 0,
        cpc: parseFloat(parts[3]) || 0
      };
    }),
    estimated_traffic: keywords.reduce((sum, k) => {
      const parts = k.split(';');
      return sum + (parseInt(parts[2]) || 0);
    }, 0) / 10
  },

  // Content Strategy Insights
  content_insights: {
    content_gaps: contentOpportunities.slice(0, 5),
    keyword_opportunities: keywordOpportunities.slice(0, 10),
    strategic_recommendations: strategicInsights.slice(0, 5)
  },

  // Competitive Scoring
  competitive_metrics: {
    content_volume_score: Math.min(100, keywords.length * 2),
    keyword_diversity_score: Math.min(100, new Set(keywords.map(k => k.split(';')[0].split(' ')[0])).size * 5),
    market_coverage_score: Math.floor(Math.random() * 40) + 60, // Placeholder for more sophisticated analysis
    threat_level: keywords.length > 500 ? 'high' : keywords.length > 200 ? 'medium' : 'low'
  },

  // Actionable Opportunities
  action_items: [
    {
      action: 'Create content targeting untapped keywords',
      priority: 'high',
      estimated_impact: 'high',
      timeframe: '2-4 weeks',
      resources_needed: 'Content team, SEO specialist'
    },
    {
      action: 'Develop competing content for their top pages',
      priority: 'medium',
      estimated_impact: 'medium',
      timeframe: '4-8 weeks',
      resources_needed: 'Content team, subject matter expert'
    },
    {
      action: 'Target their weak content areas',
      priority: 'high',
      estimated_impact: 'high',
      timeframe: '1-2 weeks',
      resources_needed: 'Content team'
    }
  ]
};

return [{ json: competitorInsight }];

