parameters.jsCode:
const { content_id, title, content, platforms, schedule_time, image_url, hashtags, target_audience } = items[0].json;

// Validate required data
if (!content_id || !title || !content) {
  throw new Error('Missing required fields: content_id, title, or content');
}

// Parse platforms (default to all if not specified)
const targetPlatforms = Array.isArray(platforms) ? platforms : 
  (typeof platforms === 'string' ? platforms.split(',').map(p => p.trim().toLowerCase()) : 
   ['facebook', 'instagram', 'linkedin', 'twitter']);

// Parse hashtags
const hashtagList = Array.isArray(hashtags) ? hashtags : 
  (typeof hashtags === 'string' ? hashtags.split(',').map(h => h.trim().startsWith('#') ? h.trim() : `#${h.trim()}`) : []);

// Determine posting time (immediate or scheduled)
const postTime = schedule_time ? new Date(schedule_time) : new Date();
const isScheduled = schedule_time && postTime > new Date();

// Generate platform-specific content variations
const platformContent = {
  facebook: {
    text: content.length > 500 ? content.substring(0, 497) + '...' : content,
    call_to_action: 'Learn more: https://theprofitplatform.com.au',
    character_limit: 63206,
    optimal_length: 500
  },
  instagram: {
    text: content.length > 300 ? content.substring(0, 297) + '...' : content,
    hashtags: hashtagList.slice(0, 30).join(' '), // Instagram allows 30 hashtags
    character_limit: 2200,
    optimal_length: 300,
    requires_image: true
  },
  linkedin: {
    text: content.length > 700 ? content.substring(0, 697) + '...' : content,
    professional_tone: true,
    character_limit: 3000,
    optimal_length: 700
  },
  twitter: {
    text: content.length > 250 ? content.substring(0, 247) + '...' : content,
    hashtags: hashtagList.slice(0, 2).join(' '), // Twitter best practice: 1-2 hashtags
    character_limit: 280,
    optimal_length: 250
  }
};

return [{
  json: {
    content_id,
    title,
    original_content: content,
    target_platforms: targetPlatforms,
    platform_content: platformContent,
    image_url: image_url || null,
    hashtags: hashtagList,
    target_audience: target_audience || 'general',
    post_time: postTime.toISOString(),
    is_scheduled: isScheduled,
    processing_timestamp: new Date().toISOString()
  }
}];

