parameters.jsCode:
// Compile publishing results from all platforms
const results = {
  content_id: $('Parse & Format Publishing Data').item.json.content_id,
  publishing_timestamp: new Date().toISOString(),
  platforms: {},
  overall_status: 'success',
  errors: [],
  total_reach_estimate: 0
};

// Process Facebook results
if (items.find(item => item.json.id && item.json.id.includes('_'))) {
  const fbResult = items.find(item => item.json.id && item.json.id.includes('_'));
  results.platforms.facebook = {
    status: 'published',
    post_id: fbResult.json.id,
    platform: 'facebook',
    reach_estimate: 500
  };
  results.total_reach_estimate += 500;
}

// Process Instagram results
if (items.find(item => item.json.id && !item.json.id.includes('_'))) {
  const igResult = items.find(item => item.json.id && !item.json.id.includes('_'));
  results.platforms.instagram = {
    status: 'published',
    post_id: igResult.json.id,
    platform: 'instagram',
    reach_estimate: 300
  };
  results.total_reach_estimate += 300;
}

// Process LinkedIn results
if (items.find(item => item.json.id && item.json.id.startsWith('urn:'))) {
  const liResult = items.find(item => item.json.id && item.json.id.startsWith('urn:'));
  results.platforms.linkedin = {
    status: 'published',
    post_id: liResult.json.id,
    platform: 'linkedin',
    reach_estimate: 200
  };
  results.total_reach_estimate += 200;
}

// Process Twitter results
if (items.find(item => item.json.data && item.json.data.id)) {
  const twitterResult = items.find(item => item.json.data && item.json.data.id);
  results.platforms.twitter = {
    status: 'published',
    post_id: twitterResult.json.data.id,
    platform: 'twitter',
    reach_estimate: 150
  };
  results.total_reach_estimate += 150;
}

// Check for any errors
if (items.some(item => item.json.error)) {
  results.overall_status = 'partial_success';
  results.errors = items
    .filter(item => item.json.error)
    .map(item => ({ platform: 'unknown', error: item.json.error }));
}

return [{ json: results }];

