const data = $json;
const timestamp = new Date().toISOString();

return [{
  json: {
    test_status: 'success',
    domain: 'theprofitplatform.com.au',
    timestamp: timestamp,
    api_test_result: {
      github_user: data.login || 'not found',
      user_id: data.id || 'unknown',
      public_repos: data.public_repos || 0
    },
    message: 'HTTP request test completed successfully!'
  }
}];
