// Parse available tools and select current one
const stdout = $input.first().json.stdout || 'rank-tracker';
const tools = stdout.trim().split('\n').filter(t => t.length > 0);

if (tools.length === 0) {
  tools.push('rank-tracker');
}

const now = new Date();
const minutesSinceEpoch = Math.floor(now.getTime() / (60 * 1000));
const currentToolIndex = minutesSinceEpoch % tools.length;
const currentTool = tools[currentToolIndex];
const nextToolIndex = (currentToolIndex + 1) % tools.length;
const nextTool = tools[nextToolIndex];

return {
  toolSlug: currentTool,
  toolName: currentTool.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' '),
  nextTool: nextTool.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' '),
  cycleCount: minutesSinceEpoch,
  timestamp: now.toISOString(),
  filePath: `/home/avi/projects/astro-site/src/pages/tools/${currentTool}.astro`,
  totalTools: tools.length,
  availableTools: tools.join(', ')
};

// COMPREHENSIVE Tool Analysis
const content = $input.first().json.stdout || '';
const prevData = $input.first().json;

if (!content || content.includes('No such file')) {
  return { skip: true, reason: 'File not found' };
}

// === CODE QUALITY ANALYSIS ===
const lines = content.split('\n');
const codeLines = lines.filter(l => l.trim() && !l.trim().startsWith('//')).length;
const commentLines = lines.filter(l => l.trim().startsWith('//')).length;
const blankLines = lines.filter(l => !l.trim()).length;
const totalLines = lines.length;

// === FEATURE DETECTION ===
const features = {
  hasMockData: content.includes('mockData') || content.includes('// Mock'),
  hasRealAPI: content.includes('fetch(') || content.includes('axios'),
  hasCharts: content.includes('chart') || content.includes('Chart.js') || content.includes('canvas'),
  hasHistory: content.includes('history') || content.includes('localStorage'),
  hasExport: content.includes('export') || content.includes('download') || content.includes('CSV'),
  hasAuth: content.includes('auth') || content.includes('token') || content.includes('session'),
  hasValidation: content.includes('validate') || content.includes('required'),
  hasErrorHandling: content.includes('try') || content.includes('catch') || content.includes('error'),
  hasLoading: content.includes('loading') || content.includes('spinner'),
  hasResponsive: content.includes('responsive') || content.includes('@media'),
  hasAccessibility: content.includes('aria-') || content.includes('role='),
  hasSEO: content.includes('meta') || content.includes('og:') || content.includes('twitter:'),
  hasAnalytics: content.includes('gtag') || content.includes('analytics'),
  hasDatabase: content.includes('db.') || content.includes('query') || content.includes('INSERT'),
  hasTypeScript: content.includes('interface') || content.includes('type '),
  hasTests: false // Would need to check separate test files
};

// === COMPONENT ANALYSIS ===
const components = {
  buttons: (content.match(/<button/g) || []).length,
  inputs: (content.match(/<input/g) || []).length,
  forms: (content.match(/<form/g) || []).length,
  images: (content.match(/<img/g) || []).length,
  links: (content.match(/<a /g) || []).length,
  scripts: (content.match(/<script/g) || []).length,
  styles: (content.match(/<style/g) || []).length
};

// === SECURITY ANALYSIS ===
const securityIssues = [];
if (content.includes('eval(')) securityIssues.push('Uses eval() - potential XSS risk');
if (content.includes('innerHTML') && !content.includes('DOMPurify')) securityIssues.push('Uses innerHTML without sanitization');
if (content.includes('http://') && !content.includes('localhost')) securityIssues.push('Contains HTTP (non-secure) URLs');
if (content.match(/password|secret|token|key/i) && content.includes('console.log')) securityIssues.push('Possible credential logging');

// === PERFORMANCE ANALYSIS ===
const performanceIssues = [];
if ((content.match(/fetch\(/g) || []).length > 3 && !content.includes('Promise.all')) performanceIssues.push('Multiple sequential API calls - consider parallelization');
if (content.includes('setInterval') && !content.includes('clearInterval')) performanceIssues.push('Potential memory leak - setInterval without clear');
if (content.match(/<img/g) && !content.includes('loading="lazy"')) performanceIssues.push('Images without lazy loading');

// === UX ANALYSIS ===
const uxIssues = [];
if (!features.hasLoading && features.hasRealAPI) uxIssues.push('API calls without loading indicators');
if (!features.hasErrorHandling && features.hasRealAPI) uxIssues.push('No error handling for API failures');
if (!features.hasValidation && components.forms > 0) uxIssues.push('Forms without validation');
if (!features.hasAccessibility) uxIssues.push('Missing accessibility features (ARIA labels)');

// === CALCULATE SCORES ===
const featureScore = Object.values(features).filter(Boolean).length;
const maxFeatures = Object.keys(features).length;
const completeness = Math.round((featureScore / maxFeatures) * 100);

const userValue = Math.round(
  (features.hasRealAPI ? 30 : 0) +
  (features.hasCharts ? 20 : 0) +
  (features.hasExport ? 15 : 0) +
  (features.hasHistory ? 15 : 0) +
  (features.hasErrorHandling ? 10 : 0) +
  (features.hasValidation ? 10 : 0)
);

const codeQuality = Math.round(
  (features.hasErrorHandling ? 25 : 0) +
  (features.hasTypeScript ? 20 : 0) +
  (commentLines > codeLines * 0.1 ? 15 : 0) +
  (features.hasValidation ? 15 : 0) +
  (securityIssues.length === 0 ? 25 : 0)
);

// === GENERATE IMPROVEMENTS ===
const improvements = [];

// CRITICAL Priority
if (features.hasMockData) {
  improvements.push({
    category: 'Data Integration',
    title: 'Replace Mock Data with Real API Integration',
    priority: 'CRITICAL',
    estimatedTime: '6-8 hours',
    impact: 'Transforms tool from demo to production-ready',
    roi: 'High - Converts demo to production tool',
    technicalDetails: 'Implement REST API endpoints, add authentication, handle rate limiting',
    actionItems: [
      'Design API schema and endpoints',
      'Implement API client with error handling',
      'Add authentication/authorization',
      'Implement rate limiting and caching',
      'Add API documentation'
    ],
    successMetrics: 'Real-time data updates, 99.9% uptime, <500ms response time'
  });
}

if (securityIssues.length > 0) {
  improvements.push({
    category: 'Security',
    title: 'Fix Security Vulnerabilities',
    priority: 'CRITICAL',
    estimatedTime: '2-4 hours',
    impact: 'Prevents data breaches and XSS attacks',
    roi: 'Critical - Protects user data and business reputation',
    technicalDetails: securityIssues.join('; '),
    actionItems: [
      'Sanitize all user inputs',
      'Implement Content Security Policy',
      'Use HTTPS for all external resources',
      'Remove credential logging',
      'Add security headers'
    ],
    successMetrics: 'Zero security vulnerabilities on penetration testing'
  });
}

// HIGH Priority
if (!features.hasCharts && !features.hasHistory) {
  improvements.push({
    category: 'User Engagement',
    title: 'Add Historical Tracking & Data Visualization',
    priority: 'HIGH',
    estimatedTime: '4-6 hours',
    impact: 'Enables trend analysis and ongoing user value',
    roi: 'High - Increases session time by 150%, retention by 85%',
    technicalDetails: 'Implement Chart.js/D3.js with localStorage or database persistence',
    actionItems: [
      'Design database schema for historical data',
      'Implement data collection and storage',
      'Build interactive charts (line, bar, pie)',
      'Add date range filters',
      'Implement data comparison features'
    ],
    successMetrics: 'Users return 3x more often, 5min avg session time increase'
  });
}

if (!features.hasErrorHandling && features.hasRealAPI) {
  improvements.push({
    category: 'Reliability',
    title: 'Implement Comprehensive Error Handling',
    priority: 'HIGH',
    estimatedTime: '3-4 hours',
    impact: 'Prevents crashes and improves user experience',
    roi: 'High - Reduces bounce rate by 40%',
    technicalDetails: 'Add try-catch blocks, fallback mechanisms, user-friendly error messages',
    actionItems: [
      'Wrap all API calls in try-catch',
      'Implement retry logic with exponential backoff',
      'Add user-friendly error messages',
      'Implement error logging and monitoring',
      'Create fallback UI states'
    ],
    successMetrics: 'Zero unhandled errors, <1% error rate'
  });
}

// MEDIUM Priority
if (!features.hasExport) {
  improvements.push({
    category: 'Data Portability',
    title: 'Add Export/Download Functionality',
    priority: 'MEDIUM',
    estimatedTime: '3-4 hours',
    impact: 'Increases tool utility and shareability',
    roi: 'Medium - Increases sharing by 250%, professional use by 180%',
    technicalDetails: 'Implement CSV/PDF/Excel export with proper formatting',
    actionItems: [
      'Add CSV export functionality',
      'Implement PDF report generation',
      'Add Excel export with formatting',
      'Create shareable report links',
      'Add email report feature'
    ],
    successMetrics: '30% of users export data, 50% share reports'
  });
}

if (performanceIssues.length > 0) {
  improvements.push({
    category: 'Performance',
    title: 'Optimize Performance & Loading Speed',
    priority: 'MEDIUM',
    estimatedTime: '3-5 hours',
    impact: 'Faster page loads, better user experience',
    roi: 'Medium - Reduces bounce rate by 25%, improves SEO',
    technicalDetails: performanceIssues.join('; '),
    actionItems: [
      'Implement lazy loading for images',
      'Use Promise.all for parallel API calls',
      'Add resource cleanup (clearInterval, removeEventListener)',
      'Implement code splitting',
      'Optimize bundle size'
    ],
    successMetrics: '<2s page load, 90+ Lighthouse score'
  });
}

// LOW Priority
if (!features.hasAccessibility) {
  improvements.push({
    category: 'Accessibility',
    title: 'Improve Accessibility (WCAG 2.1)',
    priority: 'LOW',
    estimatedTime: '2-3 hours',
    impact: 'Makes tool usable for users with disabilities',
    roi: 'Low-Medium - Expands audience by 15%, improves SEO',
    technicalDetails: 'Add ARIA labels, keyboard navigation, screen reader support',
    actionItems: [
      'Add ARIA labels to all interactive elements',
      'Implement keyboard navigation',
      'Add focus indicators',
      'Ensure color contrast ratios',
      'Test with screen readers'
    ],
    successMetrics: 'WCAG 2.1 AA compliance, accessible to all users'
  });
}

if (!features.hasSEO) {
  improvements.push({
    category: 'SEO',
    title: 'Enhance SEO & Social Sharing',
    priority: 'LOW',
    estimatedTime: '2-3 hours',
    impact: 'Improves discoverability and social sharing',
    roi: 'Medium - Increases organic traffic by 40%',
    technicalDetails: 'Add meta tags, Open Graph, Twitter Cards, schema.org',
    actionItems: [
      'Add comprehensive meta tags',
      'Implement Open Graph tags',
      'Add Twitter Card metadata',
      'Create schema.org structured data',
      'Optimize page titles and descriptions'
    ],
    successMetrics: 'Rich previews on all platforms, 40% traffic increase'
  });
}

if (improvements.length === 0) {
  return { skip: true, reason: 'No improvements needed - tool is production-ready!' };
}

// === BUILD COMPREHENSIVE REPORT ===
return {
  toolName: prevData.toolName,
  toolSlug: prevData.toolSlug,
  timestamp: prevData.timestamp,
  cycleCount: prevData.cycleCount,
  nextTool: prevData.nextTool,
  totalTools: prevData.totalTools,
  availableTools: prevData.availableTools,

  // Scores
  completeness,
  userValue,
  codeQuality,

  // Code Statistics
  codeStats: {
    totalLines,
    codeLines,
    commentLines,
    blankLines,
    commentRatio: Math.round((commentLines / codeLines) * 100)
  },

  // Features
  features,
  featureCount: featureScore,
  maxFeatures,

  // Components
  components,

  // Issues
  securityIssues,
  performanceIssues,
  uxIssues,

  // Improvements
  improvements,
  improvementCount: improvements.length,

  // Priority breakdown
  criticalCount: improvements.filter(i => i.priority === 'CRITICAL').length,
  highCount: improvements.filter(i => i.priority === 'HIGH').length,
  mediumCount: improvements.filter(i => i.priority === 'MEDIUM').length,
  lowCount: improvements.filter(i => i.priority === 'LOW').length
};

// Generate Comprehensive Email HTML Report
const d = $input.item.json;

const priorityClass = (p) => `priority-${p.toLowerCase()}`;
const priorityEmoji = (p) => {
  const map = { 'CRITICAL': 'üö®', 'HIGH': '‚ö†Ô∏è', 'MEDIUM': 'üìã', 'LOW': 'üí°' };
  return map[p] || 'üìå';
