// Tool Improvement Agent - Tool Rotation Logic
const tools = [
  'rank-tracker',
  'revenue-leak-detector', 
  'speed-test',
  'website-speed-test',
  'keyword-difficulty-checker',
  'local-rankings-map',
  'seo-audit-tool'
];

// Get current cycle from workflow static data or start at 0
const currentCycle = $workflow.staticData.cycleCount || 0;
const currentToolIndex = currentCycle % tools.length;
const currentTool = tools[currentToolIndex];
const nextTool = tools[(currentToolIndex + 1) % tools.length];

// Increment cycle count
$workflow.staticData.cycleCount = currentCycle + 1;

return {
  json: {
    toolSlug: currentTool,
    toolName: currentTool.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' '),
    nextTool: nextTool.split('-').map(w => w.charAt(0).toUpperCase() + w.slice(1)).join(' '),
    cycleCount: currentCycle + 1,
    timestamp: new Date().toISOString()
  }
};

// Analyze Tool Content
const content = $input.item.binary?.data ? Buffer.from($input.item.binary.data.data).toString('utf-8') : '';
const toolName = $json.toolName;
const toolSlug = $json.toolSlug;

if (!content) {
  return { json: { skip: true, reason: 'File not found' } };
}

// Detect tool type
function detectType(name, content) {
  if (content.includes('ranking') || content.includes('SERP')) return 'seo';
  if (content.includes('speed') || content.includes('performance')) return 'performance';
  if (content.includes('revenue') || content.includes('leak')) return 'analytics';
  if (content.includes('keyword')) return 'seo';
  return 'utility';
}

// Calculate completeness score
function calculateCompleteness(content) {
  let score = 30;
  if (content.includes('export') || content.includes('download')) score += 8;
  if (content.includes('localStorage') || content.includes('database')) score += 8;
  if (content.includes('chart') || content.includes('graph')) score += 8;
  if (content.includes('api') || content.includes('fetch') && !content.includes('mockData')) score += 8;
  if (content.includes('PDF') || content.includes('CSV')) score += 8;
  if (content.includes('authentication') || content.includes('auth')) score += 10;
  if (content.includes('real-time') || content.includes('websocket')) score += 10;
  if (content.includes('try') && content.includes('catch')) score += 5;
  if (content.includes('loading') && content.includes('error')) score += 5;
  if (content.includes('aria-')) score += 5;
  return Math.min(score, 100);
}

// Calculate user value score
function calculateUserValue(content) {
  let score = 40;
  if (content.includes('real-time') || content.includes('live')) score += 15;
  if (content.includes('export') || content.includes('download')) score += 10;
  if (content.includes('share') || content.includes('email')) score += 8;
  if (content.includes('historical') || content.includes('trend')) score += 12;
  if (content.includes('save') || content.includes('bookmark')) score += 5;
  if (content.includes('tutorial') || content.includes('help')) score += 5;
  return Math.min(score, 100);
}

// Suggest APIs based on tool type
function suggestAPIs(toolSlug) {
  if (toolSlug.includes('rank') || toolSlug.includes('serp')) {
    return [
      'SerpAPI: Google search result scraping',
      'DataForSEO: SERP data + competitor analysis',
      'ValueSERP: Cost-effective SERP data'
    ];
  }
  if (toolSlug.includes('speed') || toolSlug.includes('performance')) {
    return [
      'PageSpeed Insights API: Core Web Vitals',
      'Lighthouse CI: Performance audits',
      'WebPageTest API: Advanced testing'
    ];
  }
  if (toolSlug.includes('keyword')) {
    return [
      'SEMrush API: Keyword difficulty',
      'Ahrefs API: Keyword metrics',
      'Moz API: Domain authority'
    ];
  }
  return ['Custom API integration'];
}

// Generate improvements
const improvements = [];

// Check for mock data
if (content.includes('mockData') || content.includes('// Mock') || content.includes('const data = [')) {
  const apis = suggestAPIs(toolSlug);
  improvements.push({
    title: 'Replace Mock Data with Real API Integration',
    priority: 'CRITICAL',
    category: 'API Integration',
    effort: 'high',
    estimatedTime: '6-8 hours',
    impact: 'Transforms tool from demo to production-ready',
    description: `Replace hardcoded mock data with live API integration. This will provide:\n- Real-time, accurate data that updates automatically\n- Professional credibility and user trust\n- Actual value to users instead of a demo\n- Competitive advantage over static tools`,
    apis: apis,
    steps: [
      `Sign up for ${apis[0]?.split(':')[0] || 'API service'}`,
      'Create .env file with API keys (never commit to git)',
      'Install required packages: npm install axios dotenv',
      'Create /src/services/api-client.js with error handling',
      'Implement rate limiting (respect API quotas)',
      'Replace all mockData arrays with API calls',
      'Add loading skeletons and error states',
      'Implement response caching (reduce API costs)',
      'Add retry logic for failed requests',
      'Test edge cases: no data, API down, rate limits'
    ],
    roi: 'High - Converts demo to production tool, increases user retention by 300%+'
  });
}

// Check for historical tracking
if (!content.includes('chart') && !content.includes('history') && !content.includes('trend')) {
  improvements.push({
    title: 'Add Historical Tracking & Charts',
    priority: 'HIGH',
    category: 'Feature Addition',
    effort: 'high',
    estimatedTime: '4-6 hours',
    impact: 'High - Enables ongoing value and user retention',
    description: `Implement historical tracking with visual charts to show trends over time. This feature will:\n- Increase user engagement and return visits\n- Provide valuable insights into changes\n- Differentiate from competitors\n- Enable data-driven decision making`,
    steps: [
      'Design UI/UX for historical charts (use Chart.js or Recharts)',
      'Set up data persistence layer (localStorage or database)',
      'Create date range selector component',
      'Implement data aggregation logic',
      'Add chart visualization component',
      'Create export chart as image functionality',
      'Write tests for data persistence',
      'Update documentation'
    ],
    roi: 'Medium-High - Increases session time by 150%, return rate by 200%'
  });
}

// Check for export functionality
if (!content.includes('export') && !content.includes('download') && !content.includes('PDF')) {
  improvements.push({
    title: 'Add Export/Download Functionality',
    priority: 'MEDIUM',
    category: 'Feature Addition',
    effort: 'medium',
    estimatedTime: '3-4 hours',
    impact: 'Medium - Increases tool utility and shareability',
    description: `Add ability to export data in multiple formats (PDF, CSV, PNG). This enables:\n- Users to share results with team/clients\n- Integration into reports and presentations\n- Offline access to data\n- Increased perceived value`,
    steps: [
      'Install jsPDF and html2canvas for PDF generation',
      'Create export button component',
      'Implement CSV export with proper formatting',
      'Add PNG screenshot export using html2canvas',
      'Create export options modal',
      'Add filename customization',
      'Test exports across browsers',
      'Add export to feature list on landing page'
    ],
    roi: 'Medium - Increases sharing by 250%, word-of-mouth referrals'
  });
}

// Check for batch processing
if ((toolSlug.includes('tracker') || toolSlug.includes('checker')) && !content.includes('batch') && !content.includes('multiple')) {
  improvements.push({
    title: 'Add Batch Processing',
    priority: 'MEDIUM',
    category: 'Feature Addition',
    effort: 'high',
    estimatedTime: '5-7 hours',
    impact: 'High - Enables power users and enterprise use',
    description: `Implement batch processing to analyze multiple items simultaneously. This provides:\n- Massive time savings for users\n- Enterprise-level functionality\n- Competitive advantage\n- Higher perceived value`,
    steps: [
      'Design bulk input UI (textarea or CSV upload)',
      'Implement queue system for API calls',
      'Add progress indicator for batch jobs',
      'Create results table with sortable columns',
      'Implement batch export functionality',
      'Add pause/resume batch processing',
      'Handle API rate limits gracefully',
      'Write batch processing tests'
    ],
    roi: 'High - Attracts enterprise users, potential for premium tier'
  });
}

const analysis = {
  toolName: toolName,
  toolSlug: toolSlug,
  type: detectType(toolName, content),
  status: 'active',
  priority: 'high',
  completeness: calculateCompleteness(content),
  userValue: calculateUserValue(content),
  improvements: improvements.slice(0, 4),
  cycleCount: $json.cycleCount,
  nextTool: $json.nextTool,
  timestamp: $json.timestamp
};

// Only continue if there are improvements
if (improvements.length === 0) {
  return { json: { skip: true, reason: 'No improvements needed' } };
}

return { json: analysis };

// Generate Professional HTML Email
const data = $json;

const html = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; line-height: 1.6; color: #333; background: #f5f5f5; margin: 0; padding: 0; }
    .container { max-width: 700px; margin: 20px auto; background: white; border-radius: 8px; overflow: hidden; box-shadow: 0 2px 8px rgba(0,0,0,0.1); }
    .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center; }
    .header h1 { margin: 0; font-size: 24px; }
    .header .cycle { font-size: 14px; opacity: 0.9; margin-top: 5px; }
    .stats { display: flex; justify-content: space-around; padding: 20px; background: #f8f9fa; border-bottom: 1px solid #e0e0e0; }
    .stat { text-align: center; }
    .stat-value { font-size: 28px; font-weight: bold; color: #667eea; }
    .stat-label { font-size: 12px; color: #666; text-transform: uppercase; }
    .progress-bar { background: #e0e0e0; height: 8px; border-radius: 4px; overflow: hidden; margin: 10px 0; }
    .progress-fill { background: linear-gradient(90deg, #667eea, #764ba2); height: 100%; }
    .content { padding: 30px; }
    .meta { display: grid; grid-template-columns: repeat(2, 1fr); gap: 15px; margin-bottom: 30px; }
    .meta-item { padding: 15px; background: #f8f9fa; border-radius: 6px; border-left: 4px solid #667eea; }
    .meta-label { font-size: 12px; color: #666; text-transform: uppercase; margin-bottom: 5px; }
    .meta-value { font-size: 16px; font-weight: 600; color: #333; }
    .improvement { margin-bottom: 30px; padding: 20px; border: 1px solid #e0e0e0; border-radius: 8px; background: #fafafa; }
    .improvement-header { display: flex; justify-content: space-between; align-items: start; margin-bottom: 15px; }
    .improvement-title { font-size: 18px; font-weight: 700; color: #333; margin: 0; }
    .priority { padding: 4px 12px; border-radius: 12px; font-size: 11px; font-weight: 700; text-transform: uppercase; }
    .priority-critical { background: #fee; color: #c00; }
    .priority-high { background: #fff3cd; color: #856404; }
    .priority-medium { background: #d1ecf1; color: #0c5460; }
    .improvement-meta { display: flex; gap: 15px; margin-bottom: 15px; font-size: 13px; flex-wrap: wrap; }
