// Parse Playwright test results
const resultsJson = JSON.parse($input.item.json.data.toString());

const stats = {
  total: resultsJson.stats.expected + resultsJson.stats.unexpected,
  passed: resultsJson.stats.expected,
  failed: resultsJson.stats.unexpected,
  duration: resultsJson.stats.duration,
  failures: []
};

// Collect failure details
function collectFailures(suite) {
  if (suite.specs) {
    suite.specs.forEach(spec => {
      if (!spec.ok && spec.tests && spec.tests[0]) {
        stats.failures.push({
          title: spec.title,
          file: spec.file,
          error: spec.tests[0].results[0]?.error?.message || 'Unknown error'
        });
      }
    });
  }
  if (suite.suites) {
    suite.suites.forEach(collectFailures);
  }
}

resultsJson.suites.forEach(collectFailures);

return {
  timestamp: new Date().toISOString(),
  runId: Date.now(),
  stats: stats,
  hasFailures: stats.failed > 0
};

// Define pages to screenshot - only actual production pages
const pages = [
  { path: '/', name: 'home' },
  { path: '/about/', name: 'about' },
  { path: '/contact/', name: 'contact' },
  { path: '/privacy/', name: 'privacy' },
  { path: '/terms/', name: 'terms' }
];

const baseUrl = 'https://theprofitplatform.com.au';
const timestamp = new Date().toISOString().replace(/:/g, '-').slice(0, 19);

return pages.map(page => ({
  ...page,
  url: `${baseUrl}${page.path}`,
  timestamp,
  screenshotDir: `/home/avi/projects/astro-site/scripts/visual-check/screenshots/run-${timestamp}`
}));

// Update summary with new run data
const newStats = $('Parse Test Results').item.json.stats;
const newRunId = $('Parse Test Results').item.json.runId;
const newTimestamp = $('Parse Test Results').item.json.timestamp;

let summary = [];
try {
  const summaryData = $input.item.json.data.toString();
  summary = JSON.parse(summaryData);
} catch (e) {
  // New summary file
  summary = [];
}

const previousRun = summary.length > 0 ? summary[summary.length - 1] : null;
const issuesDelta = previousRun ? newStats.failed - previousRun.totalIssues : 0;

const status = previousRun
  ? (issuesDelta < 0 ? 'improved' : issuesDelta > 0 ? 'degraded' : 'unchanged')
  : 'initial';

const newEntry = {
  runId: newRunId,
  timestamp: newTimestamp,
  totalIssues: newStats.failed,
  fixesGenerated: 0,
  status,
  issuesDelta,
  testsPassed: newStats.passed,
  testsTotal: newStats.total,
  duration: newStats.duration,
  note: 'Playwright-based validation (n8n automated)'
};

summary.push(newEntry);

// Keep only last 100 runs
if (summary.length > 100) {
  summary = summary.slice(-100);
}

return {
  summary,
  latestRun: newEntry,
  summaryJson: JSON.stringify(summary, null, 2)
};

// Generate comprehensive HTML email report with updated monitoring configuration info
const runData = $('Update Summary Data').item.json;
const stats = $('Parse Test Results').item.json.stats;
const latestRun = runData.latestRun;
const summary = runData.summary || [];

// Status emoji
const statusEmoji = latestRun.status === 'improved' ? '‚úÖ' :
                   latestRun.status === 'degraded' ? '‚ö†Ô∏è' : 'üìä';

// Calculate pass rate
const passRate = Math.round((latestRun.testsPassed / latestRun.testsTotal) * 100);

// Get trend data (last 5 runs)
const recentRuns = summary.slice(-5);
const trendData = recentRuns.map(r => ({
  timestamp: new Date(r.timestamp).toLocaleTimeString(),
  issues: r.totalIssues,
  status: r.status
}));

// Categorize failures
const failureCategories = {
  http: [],
  html: [],
  css: [],
  js: []
};

stats.failures.forEach(f => {
  if (f.title.includes('HTTP') || f.title.includes('returns 200')) {
    failureCategories.http.push(f);
  } else if (f.title.includes('<main>') || f.title.includes('<h1>')) {
    failureCategories.html.push(f);
  } else if (f.title.includes('CSS')) {
    failureCategories.css.push(f);
  } else if (f.title.includes('JavaScript') || f.title.includes('JS')) {
    failureCategories.js.push(f);
  }
});

// Build comprehensive HTML
const html = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8">
  <style>
    body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; background: #f5f5f5; margin: 0; padding: 20px; }
    .container { max-width: 800px; margin: 0 auto; background: white; border-radius: 8px; box-shadow: 0 2px 8px rgba(0,0,0,0.1); overflow: hidden; }
    .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center; }
    .header h1 { margin: 0; font-size: 28px; }
    .header p { margin: 10px 0 0; opacity: 0.9; }
    .info-banner { background: #cce5ff; border-left: 4px solid #004085; padding: 15px; margin: 20px; border-radius: 5px; }
    .stats { display: grid; grid-template-columns: repeat(auto-fit, minmax(150px, 1fr)); gap: 15px; padding: 30px; }
    .stat { background: #f8f9fa; padding: 20px; border-radius: 6px; text-align: center; }
    .stat-value { font-size: 32px; font-weight: bold; color: #667eea; margin-bottom: 5px; }
    .stat-label { font-size: 14px; color: #666; text-transform: uppercase; letter-spacing: 0.5px; }
    .status-badge { display: inline-block; padding: 5px 15px; border-radius: 20px; font-size: 12px; font-weight: bold; text-transform: uppercase; }
    .status-improved { background: #d4edda; color: #155724; }
    .status-degraded { background: #f8d7da; color: #721c24; }
    .status-unchanged { background: #d1ecf1; color: #0c5460; }
    .failures { padding: 0 30px 30px; }
    .failure-item { background: #fff3cd; border-left: 4px solid #ffc107; padding: 15px; margin-bottom: 10px; border-radius: 4px; }
    .failure-title { font-weight: bold; color: #856404; margin-bottom: 5px; }
    .failure-error { font-family: monospace; font-size: 12px; color: #666; }
    .footer { background: #f8f9fa; padding: 20px; text-align: center; font-size: 12px; color: #666; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>${statusEmoji} Visual Quality Report</h1>
      <p>${new Date(latestRun.timestamp).toLocaleString()}</p>
      <span class="status-badge status-${latestRun.status}">${latestRun.status}</span>
    </div>

    <div class="info-banner">
      <strong>üìä Optimized Monitoring:</strong> Tests validate bundled Astro CSS (/_astro/*.css), modern architecture, and production pages only. Legacy individual CSS checks removed.
    </div>

    <div class="stats">
      <div class="stat">
        <div class="stat-value">${latestRun.totalIssues}</div>
        <div class="stat-label">Real Issues</div>
      </div>
      <div class="stat">
        <div class="stat-value">${latestRun.testsPassed}/${latestRun.testsTotal}</div>
        <div class="stat-label">Tests (${passRate}%)</div>
      </div>
      <div class="stat">
        <div class="stat-value">${latestRun.issuesDelta >= 0 ? '+' : ''}${latestRun.issuesDelta}</div>
        <div class="stat-label">Delta</div>
      </div>
      <div class="stat">
        <div class="stat-value">${(latestRun.duration / 1000).toFixed(1)}s</div>
        <div class="stat-label">Duration</div>
      </div>
    </div>

    ${stats.failures.length > 0 ? `
    <div style="padding: 30px;">
      <h2 style="color: #667eea; border-bottom: 2px solid #667eea; padding-bottom: 10px;">üìã Issue Breakdown</h2>

      ${failureCategories.http.length > 0 ? `
      <div style="margin: 20px 0;">
        <h3 style="color: #dc3545;">üî¥ HTTP Errors (${failureCategories.http.length})</h3>
        ${failureCategories.http.map(f => `
          <div class="failure-item">
            <div class="failure-title">${f.title}</div>
            <div class="failure-error">${f.error}</div>
          </div>
        `).join('')}
      </div>
      ` : ''}

      ${failureCategories.html.length > 0 ? `
      <div style="margin: 20px 0;">
        <h3 style="color: #fd7e14;">‚ö†Ô∏è Missing HTML Elements (${failureCategories.html.length})</h3>
        ${failureCategories.html.map(f => `
          <div class="failure-item">
            <div class="failure-title">${f.title}</div>
            <div class="failure-error">${f.error}</div>
          </div>
        `).join('')}
      </div>
      ` : ''}

      ${failureCategories.css.length > 0 ? `
      <div style="margin: 20px 0;">
        <h3 style="color: #17a2b8;">üé® CSS Issues (${failureCategories.css.length})</h3>
        ${failureCategories.css.map(f => `
          <div class="failure-item">
            <div class="failure-title">${f.title}</div>
            <div class="failure-error">${f.error}</div>
          </div>
        `).join('')}
      </div>
      ` : ''}

      ${failureCategories.js.length > 0 ? `
      <div style="margin: 20px 0;">
        <h3 style="color: #ffc107;">‚ö° JavaScript Issues (${failureCategories.js.length})</h3>
        ${failureCategories.js.map(f => `
          <div class="failure-item">
            <div class="failure-title">${f.title}</div>
            <div class="failure-error">${f.error}</div>
          </div>
        `).join('')}
      </div>
      ` : ''}
    </div>
    ` : '<div style="padding: 30px; text-align: center; color: #28a745;"><h3>‚úÖ All tests passed!</h3></div>'}

    ${trendData.length > 1 ? `
    <div style="padding: 30px; background: #f8f9fa;">
      <h2 style="color: #667eea; border-bottom: 2px solid #667eea; padding-bottom: 10px;">üìà Recent Trend</h2>
      <table style="width: 100%; margin-top: 15px; border-collapse: collapse;">
        <thead>
          <tr style="background: #e9ecef;">
            <th style="padding: 10px; text-align: left;">Time</th>
            <th style="padding: 10px; text-align: center;">Issues</th>
            <th style="padding: 10px; text-align: center;">Status</th>
          </tr>
        </thead>
        <tbody>
          ${trendData.map(d => `
            <tr style="border-bottom: 1px solid #dee2e6;">
              <td style="padding: 10px;">${d.timestamp}</td>
              <td style="padding: 10px; text-align: center; font-weight: bold;">${d.issues}</td>
              <td style="padding: 10px; text-align: center;">
                <span class="status-badge status-${d.status}">${d.status}</span>
              </td>
            </tr>
          `).join('')}
        </tbody>
      </table>
    </div>
    ` : ''}

    <div style="padding: 30px;">
      <h2 style="color: #667eea; border-bottom: 2px solid #667eea; padding-bottom: 10px;">üîç What's Being Tested</h2>
      <ul style="line-height: 1.8;">
        <li>‚úÖ <strong>HTTP Status Codes:</strong> Validates all pages return 200</li>
        <li>‚úÖ <strong>Semantic HTML:</strong> Checks for &lt;main&gt; and &lt;h1&gt; elements</li>
        <li>‚úÖ <strong>Bundled CSS:</strong> Validates /_astro/*.css files load successfully</li>
        <li>‚úÖ <strong>JavaScript Loading:</strong> Confirms critical JS files are loaded</li>
      </ul>
    </div>

    <div class="footer">
      <strong>Automated by n8n Visual Quality Monitoring (Optimized Config)</strong><br>
      Run ID: ${latestRun.runId} | ${new Date(latestRun.timestamp).toLocaleString()}<br>
      <a href="https://theprofitplatform.com.au" style="color: #667eea; text-decoration: none;">theprofitplatform.com.au</a>
    </div>
  </div>
</body>
</html>
`;

return {
  html,
  subject: `${statusEmoji} Visual Check - ${latestRun.totalIssues} issues | ${passRate}% pass (${latestRun.status.toUpperCase()})`,
  stats
};

// Log error details
const error = $input.item.json;
console.error('Visual Agent Error:', error);

return {
  error: true,
  message: 'Test execution failed',
  timestamp: new Date().toISOString(),
  details: error
};
